-- ----------------------------
-- Table structure for paper_insight
-- ----------------------------
DROP TABLE IF EXISTS `paper_insight`;
CREATE TABLE `paper_insight`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `paper_id` bigint NOT NULL COMMENT '论文ID',
  `user_id` bigint NULL DEFAULT NULL COMMENT '用户ID',
  `summary_markdown` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT 'AI生成的摘要总结(Markdown)',
  `innovation_points` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '创新点',
  `methods` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '方法论',
  `score` int NULL DEFAULT NULL COMMENT '评分 (0-100)',
  `score_details` json NULL COMMENT '评分详情JSON，包含各维度评分和理由',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `is_delete` tinyint NOT NULL DEFAULT 0 COMMENT '是否删除',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_paperId`(`paper_id` ASC) USING BTREE,
  INDEX `idx_userId`(`user_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 22 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '论文AI分析结果表' ROW_FORMAT = Dynamic;

